import './App.css';
import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState(null);
  const [selectedCity, setSelectedCity] = useState('');
  const [clueIndex, setClueIndex] = useState(0);
  const [showFunFact, setShowFunFact] = useState(false);
  const [showTrivia, setShowTrivia] = useState(false);
  const [dataIndex, setDataIndex] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/getData')
      .then((response) => {
        response.json().then(data => {
          setData(data);
        });
      });
  }, []);

  useEffect(() => {
    if (clueIndex >= 1 && selectedCity !== data[dataIndex].city) {
      setShowTrivia(true);
      setShowFunFact(true);
    }
  }, [clueIndex, selectedCity, data, dataIndex]);

  const handleCityChange = (event) => {
    const selectedCity = event.target.value;
    setSelectedCity(selectedCity);
    if (data && data.length > 0) {
      if (selectedCity === data[dataIndex].city) {
        console.log("correct");
        setClueIndex(0);
        setDataIndex((prevIndex) => (prevIndex + 1) % data.length); // Move to the next set of data
        setShowTrivia(false);
        setShowFunFact(false);
      } else {
        setClueIndex((prevIndex) => {
          const newIndex = Math.min(prevIndex + 1, data[dataIndex].clues.length - 1);
          return newIndex;
        });
      }
    }
  };

  return (
    <div className="App bg-dark vh-100 d-flex justify-content-center align-items-center">
      <header className="App-header">
        <h1> Guess the Country !! </h1>

        <div className='container bg-dark p-5 rounded bg-opacity-50'>
          <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Clues </h2>
              {data && <p className='text-white'>{data[dataIndex].clues[clueIndex]}</p>}
            </div>

            <div className='col-12'>
              <h2 className='text-white'> Select a City </h2>
              <select onChange={handleCityChange} className='form-select'>
                <option value="">Select a city</option>
                {data && data.map((item, index) => (
                  <option key={index} value={item.city}>{item.city}</option>
                ))}
              </select>
            </div>
          </div>
          {showTrivia && <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Trivia </h2>
              <p className='text-white'>{data[dataIndex].trivia}</p>
            </div>
          </div>}
          {showFunFact && <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Fun Fact </h2>
              <p className='text-white'>{data[dataIndex].fun_fact}</p>
            </div>
          </div>}
        </div>
      </header>
    </div>
  );
}

export default App;
