import './App.css';
import React, { useEffect, useState} from 'react';


function App() {
  const [data, setData] = useState(null);
  const [selectedCity, setSelectedCity] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/getData')
      .then((response )=>{
        response.json().then(data => {
          setData(data);
          data.forEach(item => {
            if (item.city) {
              setSelectedCity(prevCities => [...prevCities, item.city]);
            }
          });
          console.log(data);
          console.log(selectedCity);
        });
      })},[]);


  return (
    <div className="App bg-dark vh-100 d-flex justify-content-center align-items-center">
      <header className="App-header">
        <h1> Guess the Country !! </h1>

        <div className='container bg-dark p-5 rounded bg-opacity-50'>
          <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Clues </h2>
              <p className='text-white'> {data ? data[0].clues[0] : 'Loading...'} </p>
            </div>



          </div>
        </div>       

      </header>
    </div>
  );
}

export default App;
