import './App.css';
import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState(null);
  const [selectedCity, setSelectedCity] = useState('');
  const [showfact, setShowfact] = useState(false);
  const [clueIndex, setClueIndex] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/getData')
      .then((response) => {
        response.json().then(data => {
          setData(data);
        });
      })
  }, []);

  const handleCityChange = (event) => {
    const selectedCity = event.target.value;
    setSelectedCity(selectedCity);
    if (data && data.length > 0) {
      if (selectedCity === data[0].city) {
        console.log("correct");
        setClueIndex(0);
      } else {
        setClueIndex((prevIndex) => Math.min(prevIndex + 1, data[0].clues.length - 1));
        console.log("wrong");
        if (clueIndex + 1 === data[0].clues.length) {
          setShowfact(true);
        }
      }
    }
  };

  return (
    <div className="App bg-dark vh-100 d-flex justify-content-center align-items-center">
      <header className="App-header">
        <h1> Guess the Country !! </h1>

        <div className='container bg-dark p-5 rounded bg-opacity-50'>
          <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Clues </h2>
              {data && <p className='text-white'>{data[0].clues[clueIndex]}</p>}
            </div>

            <div className='col-12'>
              <h2 className='text-white'> Select a City </h2>
              <select onChange={handleCityChange} className='form-select'>
                <option value="">Select a city</option>
                {data && data.map((item, index) => (
                  <option key={index} value={item.city}>{item.city}</option>
                ))}
              </select>
            </div>

            {showfact && <div className='col-12'>
              <h2 className='text-white'> The Funny Facts are </h2>
              {data && <ul className='text-white'>
                {data[0].fun_fact.map((fact, index) => (
                  <li className='text-start' key={index}>{fact}</li>
                ))}
              </ul>}
            </div>}

            {showfact && <div className='col-12'>
              <h2 className='text-white'> The Trivia is </h2>
              {data && <ul className='text-white'>
                {data[0].trivia.map((fact, index) => (
                  <li className='text-start' key={index}>{fact}</li>
                ))}
              </ul>}
            </div>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
