import './App.css';
import React, { useEffect, useState} from 'react';


function App() {
  const [data, setData] = useState(null);
  const [selectedCity, setSelectedCity] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/getData')
      .then((response )=>{
        response.json().then(data => {
          setData(data);
          data.map((item,index)=>{
            setSelectedCity([...selectedCity, item.cities[index]]);
          })

          console.log(data[0].clues[0]);
        });
      })},[]);


  return (
    <div className="App bg-dark vh-100 d-flex justify-content-center align-items-center">
      <header className="App-header">
        <h1> Guess the Country !! </h1>

        <div className='container bg-dark p-5 rounded bg-opacity-50'>
          <div className='row'>
            <div className='col-12'>
              <h2 className='text-white'> Clues </h2>
              <p className='text-white'> {data ? data[0].clues[0] : 'Loading...'} </p>
            </div>

            <div className='col-12'>
              {data && data[0].cities.map((city, index) => (
                <div key={index} className="form-check">
                  <input className="form-check-input" type="radio" name="cityOptions" id={`cityOption${index}`} value={city} />
                  <label className="form-check-label text-white" htmlFor={`cityOption${index}`}>
                    {city}
                  </label>
                </div>
              ))}
            </div>

          </div>
        </div>       

      </header>
    </div>
  );
}

export default App;
