import './App.css';
import React, { useEffect, useState} from 'react';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/getData')
      .then((response )=>{
        response.json().then(data => {
          setData(data);
          console.log(data);
        });
      })}
  , []);


  const [score, setScore] = useState({ correct: 0, incorrect: 0 });
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [feedback, setFeedback] = useState(null);

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setScore({ ...score, correct: score.correct + 1 });
      setFeedback('ðŸŽ‰ Correct Answer! Fun Fact: ...');
      // Add confetti animation here
    } else {
      setScore({ ...score, incorrect: score.incorrect + 1 });
      setFeedback('ðŸ˜¢ Incorrect Answer! Fun Fact: ...');
      // Add sad-face animation here
    }
  };

  const playAgain = () => {
    setSelectedDestination(null);
    setFeedback(null);
    // Load a different random destination
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1> Globetrotter </h1>
        <h2> Data from the backend: </h2>
        <p>{JSON.stringify(data)}</p>
        {selectedDestination ? (
          <div>
            <h3>Random Clue: ...</h3>
            <button onClick={() => handleAnswer(true)}>Destination 1</button>
            <button onClick={() => handleAnswer(false)}>Destination 2</button>
            <button onClick={() => handleAnswer(false)}>Destination 3</button>
            <div>{feedback}</div>
            <button onClick={playAgain}>Play Again</button>
          </div>
        ) : (
          <button onClick={() => setSelectedDestination('someDestination')}>Start Game</button>
        )}
        <div>
          <h3>Score</h3>
          <p>Correct: {score.correct}</p>
          <p>Incorrect: {score.incorrect}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
